package ParameterTest;import org.testng.Assert;import org.testng.annotations.DataProvider;import org.testng.annotations.Test;/** * @program: testNgDemo2 * @description: @DataProvider示例，传递一个int参数 * @author: gonghuihui * @create: 2018-07-26 13:59 **/public class TestParameterDataProvider {    @Test(dataProvider = "provideNumbers")    public void test(int number, int expected){        Assert.assertEquals(number + 10, expected);    }    @DataProvider(name = "provideNumbers")    public Object[][] provideData(){        return new Object[][]{ {10, 20}, {100, 110}, {200, 210} };    }}