package ParameterTest;import org.testng.Assert;import org.testng.annotations.DataProvider;import org.testng.annotations.Test;import java.lang.reflect.Method;/** * @program: testNgDemo2 * @description: 根据测试方法名称传递不同的参数 * @author: gonghuihui * @create: 2018-07-26 16:03 **/public class TestParameterDataProvider3 {    @Test(dataProvider = "dataProvider")    public void test1(int number, int expected) {        Assert.assertEquals(number, expected);    }    @Test(dataProvider = "dataProvider")    public void test2(String email, String expected) {        Assert.assertEquals(email, expected);    }    @DataProvider(name = "dataProvider")    public Object[][] provideData(Method method) {        Object[][] result = null;        if (method.getName().equals("test1")) {            result = new Object[][] {                    {1, 1}, {200, 200}            };        } else if (method.getName().equals("test2")) {            result = new Object[][] {                    {"test2@gmail.com","test2@gmail.com"},                    {"test2@163.com","test2@163.com"}            };        }        return result;    }}