package ParameterTest;import org.testng.Assert;import org.testng.ITestContext;import org.testng.annotations.DataProvider;import org.testng.annotations.Test;/** * @program: testNgDemo2 * @description: 根据包含的分组名称传递参数 * @author: gonghuihui * @create: 2018-07-26 16:21 **/public class TestParameterDataProvider4 {    @Test(dataProvider = "dataProvoder", groups = {"groupA"})    public void test1(int number){        Assert.assertEquals(number,1);    }    @Test(dataProvider = "dataProvider", groups = {"groupB"})    public void test2(int number){        Assert.assertEquals(number, 2);    }    @DataProvider(name = "dataProvider")    public Object[][] provideData(ITestContext context){        Object[][] result = null;        for (String group : context.getIncludedGroups()) {            System.out.println("group : " + group);            if ("groupA".equals(group)) {                result = new Object[][] { { 1 } };                break;            }        }        if (result == null) {            result = new  Object[][] {  { 2 } };        }        return result;    }}