package ParameterTest;import org.testng.annotations.Parameters;import org.testng.annotations.Test;import java.io.FileInputStream;import java.io.FileNotFoundException;import java.io.IOException;import java.io.InputStream;import java.sql.Connection;import java.sql.DriverManager;import java.sql.SQLException;import java.util.Properties;/** * @program: testNgDemo2 * @description: 参数化测试,filename属性从xml中传递 * @author: gonghuihui * @create: 2018-07-25 16:02 **/public class TestParameterXML {    Connection con;    @Test    @Parameters({ "dbconfig", "poolsize" })    public void creatConnection(String dbconfig, int poolsize){        System.out.println("dbconfig: +" + dbconfig);        System.out.println("poolsizi: " + poolsize);        Properties prop = new Properties();        InputStream input = null;        try {            String path = System.getProperty("user.dir") + "/" + dbconfig;            System.out.println("path => " + path);            prop.load(new FileInputStream(dbconfig));            String drivers = prop.getProperty("jdbc.driver");            String connectionURL = prop.getProperty("jdbc.url");            String username = prop.getProperty("jdbc.username");            String password = prop.getProperty("jdbc.password");            System.out.println("drivers : " + drivers);            System.out.println("connectionURL : " + connectionURL);            System.out.println("username : " + username);            System.out.println("password : " + password);            Class.forName(drivers);            con = DriverManager.getConnection(connectionURL, username, password);        } catch (SQLException e) {            e.printStackTrace();        } catch (FileNotFoundException e) {            e.printStackTrace();        } catch (IOException e) {            e.printStackTrace();        } catch (ClassNotFoundException e) {            e.printStackTrace();        }    }}