package Selenium;import org.openqa.selenium.WebDriver;import org.openqa.selenium.chrome.ChromeDriver;import org.openqa.selenium.chrome.ChromeOptions;import org.testng.AssertJUnit;import org.testng.annotations.Test;/** * @program: testNgDemo2 * @description: 打开浏览器 * @author: gonghuihui * @create: 2018-07-27 11:36 **/public class TestMultipleThreads {    @Test(invocationCount = 10, threadPoolSize = 3)    public void loadTestThisWebsite(){        ChromeOptions options = new ChromeOptions();        options.addArguments("--test-type", "--ignore-certificate-errors");        System.setProperty("webdriver.chrome.driver", "/Users/gonghuihui55/worksoftwares/chromedriver");        WebDriver driver = new ChromeDriver(options);        driver.get("https://www.google.com");        System.out.println("Page Title is " + driver.getTitle());        AssertJUnit.assertEquals("Google", driver.getTitle());        driver.quit();    }}