import org.testng.annotations.AfterGroups;import org.testng.annotations.AfterMethod;import org.testng.annotations.BeforeGroups;import org.testng.annotations.BeforeMethod;/** * @program: testNgDemo2 * @description: * @author: gonghuihui * @create: 2018-07-23 17:48 **/public class TestConfiguration {    @BeforeGroups    public void beforeGroups(){        System.out.println("BeforeGroups");    }    @AfterGroups    public void afterGroups(){        System.out.println("AfterGroups");    }    @BeforeMethod    public void beforeMethod(){        System.out.println("BeforeMethod");    }    @AfterMethod    public void afterMethod(){        System.out.println("AfterMethod");    }}